    <LinearGradient colors={["red", "yellow"]} style={styles.Container}>
       {/* Step 5 */}
       <ImageBackground
        source={require('../Kahve/images/kus.jpg')}
        resizeMode="cover"
        style={styles.Container}
        imageStyle={styles.backgroundImage}
      >
        <Start />
      </ImageBackground>
    </LinearGradient>


  );
}
const styles = StyleSheet.create({
  Container: {
    flex: 1,
     backgroundColor: 'yellow'
  },

    backgroundImage: {
     opacity: 0.55
  }
});
