 import { View,StyleSheet, TextInput } from "react-native"
import PrimaryButton from "../components/PrimaryButton"

export default function Start() {
  return (
     <View style={styles.container}>
        <TextInput />
        <PrimaryButton>Ekle</PrimaryButton>
        <PrimaryButton>Sil</PrimaryButton>
     </View>
  )
}
const styles = StyleSheet.create({
   container: {
      justifyContent: 'center',
      alignItems: 'center',
      // yukardaki ikili beraber kullanılmalı
      marginTop: 100,
      marginHorizontal: 16,
      padding: 16,
      backgroundColor: 'purple',
      borderRadius: 8,
      elevation:  7,
    },
    textInput: {
        height: 50,
        width: 50,
        fontSize: 32,
        borderBottomColor: 'yellow',
        borderBottomWidth: 2,
        color: 'yellow',
        marginVertical: 8,
        fontWeight: 'bold',
        textAlign: 'center',
      },
 })
